        /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

       /* Only notify MMOC if phone is currently online or powerup
       */
        if( !cm_is_valid_state_for_sub_proc(ph_ptr->oprt_mode))
        {
          break;
        }

        /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/
