/*===========================================================================
FUNCTION cmph_update_current_acq_fail_cnt

DESCRIPTION
 Updates cmph_update_current_acq_fail_cnt
 

DEPENDENCIES
  None

RETURN VALUE
  None
SIDE EFFECTS
  none

===========================================================================*/
void cmph_update_current_acq_fail_cnt( boolean is_reset )
{

  cmph_s_type*               ph_ptr = cmph_ptr();
  uint32                     write_size;
  
  if( is_reset == TRUE )
  {
    ph_ptr->current_acq_fail_cnt = 0;
  }
  else
  {
    ph_ptr->current_acq_fail_cnt++;
  }

  /* Write to NV. This count needs to maintained across power cycles and PSM sleep/wakeups */
  write_size = sizeof (ph_ptr->current_acq_fail_cnt);
  
  if ( cmefs_write( CMEFS_CURRENT_ACQ_FAIL_CNT,
                   (byte* )&ph_ptr->current_acq_fail_cnt, write_size) <= 0)
  {
    CM_ERR_0 ("CURRENT_ACQ_FAIL_CNT write failed");
  }
  
}
/*===========================================================================
FUNCTION cmph_check_and_enter_full_rat_mode

DESCRIPTION
 Enforce full RAT if current_acq_fail_cnt reaches full_rat_acq_fail_cnt
 

DEPENDENCIES
  None

RETURN VALUE
  None
SIDE EFFECTS
  none

===========================================================================*/
boolean cmph_check_and_enter_full_rat_mode( void )
{

  cmph_s_type*               ph_ptr = cmph_ptr();
  cm_orig_q_s_type *pm_rest_obj_ptr = cmtask_orig_para_search_act_type( CM_SS_MAIN,
                                                            CM_ACT_TYPE_POLICYMAN_RESTRICT);
    
  if( ph_ptr->current_acq_fail_cnt >= ph_ptr->full_rat_acq_fail_cnt &&
      pm_rest_obj_ptr == NULL )
  {

    CM_MSG_HIGH_1("Enter full_rat_search mode acq_fail_cnt=%d", ph_ptr->current_acq_fail_cnt);
    (void) cmph_force_pref_on_the_fly(CM_SS_MAX,
                                       ph_ptr,
                                       SD_SS_PREF_REAS_USER,
                                       CM_ACT_TYPE_POLICYMAN_RESTRICT,
                                       ph_ptr->main_stack_info.pref_info.pref_term,
                                       ph_ptr->main_stack_info.pref_info.mode_pref,
                                       ph_ptr->main_stack_info.pref_info.band_pref,
                                       ph_ptr->main_stack_info.pref_info.lte_band_pref,
                                       ph_ptr->main_stack_info.pref_info.tds_band_pref,
                                       ph_ptr->main_stack_info.pref_info.plmn,
                                       CM_SRV_DOMAIN_PREF_CS_ONLY,
                                       CM_NETWORK_SEL_MODE_PREF_LIMITED_SRV,
                                       (cm_act_id_type) cmpm_ptr(),
                                       CM_ACT_UPDATE_REAS_ACT_START,
                                       TRUE,
                                       cmph_get_acq_pri_order_per_sub(CM_SS_MAIN),
                                       CM_DEFAULT_VALUE,
                                       SYS_CSG_ID_INVALID,
                                       ph_ptr->main_stack_info.pref_info.csg_rat,
                                       ph_ptr->main_stack_info.pref_info.ciot_lte_pref
                                       );

  }

  if( ph_ptr->current_acq_fail_cnt >= ph_ptr->full_rat_acq_fail_cnt )
  {
    return TRUE;
  }

  return FALSE;
  
}

/*===========================================================================
FUNCTION cmph_increment_acq_fail_cnt

DESCRIPTION
 Increase acq_fail cnt
 Write acq_fail cnt to NV
 Enforce full RAT if current_acq_fail_cnt reaches full_rat_acq_fail_cnt

DEPENDENCIES
  None

RETURN VALUE
  None
SIDE EFFECTS
  none

===========================================================================*/
void cmph_increment_acq_fail_cnt(
    boolean         is_scan_attempted
)
{

  cmph_s_type*               ph_ptr = cmph_ptr();

  /* Increment acq_fail cnt is it is not a duplicate indication */
  if( cmph_is_gw_subsc_avail(SYS_MODEM_AS_ID_1) == TRUE && 
      is_scan_attempted == TRUE && 
      ph_ptr->current_acq_fail_cnt < ph_ptr->full_rat_acq_fail_cnt )
  {
    cmph_update_current_acq_fail_cnt(FALSE);
    CM_MSG_HIGH_1("Increased current_acq_fail_cnt=%d", ph_ptr->current_acq_fail_cnt);
  }

  if( cmph_is_gw_subsc_avail(SYS_MODEM_AS_ID_1) == TRUE )
  {
    /* enter full RAT mode if current_acq_fail_cnt reaches full_rat_acq_fail_cnt */
    cmph_check_and_enter_full_rat_mode();
  }
}

/*===========================================================================
FUNCTION cmph_check_and_exit_full_rat_mode

DESCRIPTION
 if device is in Full RAT, enforce Full RAT exit
 

DEPENDENCIES
  None

RETURN VALUE
  None
SIDE EFFECTS
  none

===========================================================================*/
boolean cmph_check_and_exit_full_rat_mode( boolean force_pref )
{

  cmph_s_type*               ph_ptr = cmph_ptr();
  cm_orig_q_s_type *pm_rest_obj_ptr = cmtask_orig_para_search_act_type( CM_SS_MAIN,
                                                            CM_ACT_TYPE_POLICYMAN_RESTRICT);
  boolean           ret_val = FALSE;

  if( pm_rest_obj_ptr != NULL )
  {
     /* Delete PM restrict object and enforce pref */
     CM_MSG_HIGH_0("Exit full_rat_search mode");
     
     if(force_pref == TRUE)
     {
       cmph_delete_update_orig_mode( CM_SS_MAIN,
                                    (cm_act_id_type) cmpm_ptr(),
                                    TRUE );
     }
     else
     {
       (void)cmtask_orig_para_delete(CM_SS_MAIN,pm_rest_obj_ptr,TRUE);
     }
     ret_val = TRUE;
  }

  cmph_update_current_acq_fail_cnt(TRUE);

  return ret_val;
}

/*===========================================================================
FUNCTION cmph_cmd_set_location_info

DESCRIPTION
 Send user mcc to NAS and force pref if device is in no_srv
 

DEPENDENCIES
  None

RETURN VALUE
  None
SIDE EFFECTS
  none

===========================================================================*/
void cmph_cmd_set_location_info(
  cm_ph_cmd_s_type            *ph_cmd_ptr
)
{

  cm_ph_cmd_info_s_type       *cmd_info_ptr    = NULL;
    /* Point at command information component */

  reg_cmd_type                *reg_ptr;
    /* Pointer to REG command buffer*/
  cmss_s_type                 *ss_ptr = cmss_ptr();
  cmph_s_type                 *ph_ptr = cmph_ptr();
  cm_orig_q_s_type            *ph_orig_ptr;
  boolean                     force_top = FALSE;
  /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

  CM_ASSERT( ph_cmd_ptr != NULL );

  /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

  cmd_info_ptr = CMD_INFO_PTR( ph_cmd_ptr );
  /* Get reg cmd. buffer and populate params.*/
  reg_ptr =  cm_reg_get_buf_else_err_fatal( CM_MCC_CHANGE_IND );

  /* Fill in parameters. */
  reg_ptr->cmd.cm_mcc_change_ind.mcc = cmd_info_ptr->user_mcc;

  CM_MSG_HIGH_1( "Send CM_MCC_CHANGE_IND, user_mcc=%d",
                 reg_ptr->cmd.cm_mcc_change_ind.mcc );

  /* Send the request to NAS */
  cm_reg_send_cmd( reg_ptr );

  /* Check if full RAT exit or a new service_req is required */
  if( !cmph_check_and_exit_full_rat_mode(TRUE) )
  {
    if( !sys_srv_status_is_srv(ss_ptr->info.srv_status) )
    {

      ph_orig_ptr = cmtask_orig_para_get_top(cmph_map_as_id_to_cm_ss(cmd_info_ptr->cmd_subs));
      if( ph_orig_ptr != NULL && ph_orig_ptr->act_type == CM_ACT_TYPE_PH_OBJ )
      {
        force_top = TRUE;
      }
      
      (void) cmph_force_pref_on_the_fly(CM_SS_MAX,
                                             ph_ptr,
                                             SD_SS_PREF_REAS_USER,
                                             CM_ACT_TYPE_PH_OBJ,
                                             ph_ptr->main_stack_info.pref_info.pref_term,
                                             ph_ptr->main_stack_info.pref_info.mode_pref,
                                             ph_ptr->main_stack_info.pref_info.band_pref,
                                             ph_ptr->main_stack_info.pref_info.lte_band_pref,
                                             ph_ptr->main_stack_info.pref_info.tds_band_pref,
                                             ph_ptr->main_stack_info.pref_info.plmn,
                                             ph_ptr->main_stack_info.pref_info.srv_domain_pref,
                                             ph_ptr->main_stack_info.pref_info.network_sel_mode_pref,
                                             (cm_act_id_type) cmph_ptr(),
                                             CM_ACT_UPDATE_REAS_ACT_START,
                                             force_top,
                                             cmph_get_acq_pri_order_per_sub(CM_SS_MAIN),
                                             CM_DEFAULT_VALUE,
                                             SYS_CSG_ID_INVALID,
                                             ph_ptr->main_stack_info.pref_info.csg_rat,
                                             ph_ptr->main_stack_info.pref_info.ciot_lte_pref
                                             );
      
    }
  }
}
