static char* cmss_strchr(const char* str, char c)
{
  int i = 0;

  if( NULL == str)
  {
    return NULL;
  }
  while(str[i] != '\0')
  {
    if(str[i] == c)
    {
      return (char *)str + i;
    }
    i++;
  }
  return NULL;
}

//convert the string to number
static int cmss_atoi(const char *str)
{
  int num = 0, i = 0;

  if( NULL == str)
  {
    return 0;
  }
  while(str[i] != '\0')
  {
    if(str[i] >= '0' && str[i] <= '9')
    {
      num = num * 10 + (str[i] - '0');
      i++;
    }
    else
    {
      break;
    }
  }
  return num;
}

