  boolean has_active_ps_call_0 = cmcall_check_if_ps_call_active(CM_SS_MAIN);

  boolean has_cs_call_1 = cmcall_check_if_gw_cs_call_active(CM_CALL_STATE_NONE,CM_SS_HYBR_2);
