      SD_ERR_0( "acq_mode");
      break;
    #endif
    /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/
    case SDSS_ACQ_MODE_EOOS_FULL2:
    case SDSS_ACQ_MODE_EOOS_FULL:
    case SDSS_ACQ_MODE_EOOS_FULL_RTN2:
