  SD_MSG_HIGH_2("Reset thrttl cntrs for ss=%d state=%d",
              ss,
              ss_ptr->gpp2.acq_thrttl.state);
  ss_ptr->gpp2.acq_thrttl.acq_count = 0;
  ss_ptr->gpp2.acq_thrttl.curr_stage = 0;

