      case SDSSSCR_CON_ELSE:
      case SDSSSCR_CON_NEST_ELSE:


        /* Beginning of ELSE block
        */
        SD_ERR_0( "SDSSSCR_CON_ELSE");

        break;

      /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

      case SDSSSCR_CON_ENDIF:
      case SDSSSCR_CON_NEST_ENDIF:

