        sdss_eng_scr_con_cont( eng_ptr, event, rtrn_iact_ptr );

        break;

      case SDSSSCR_CON_CONT_TELE_BSR:
      case SDSSSCR_CON_CONT_LONG_BSR:
        sdss_eng_scr_con_cont( eng_ptr, event, rtrn_iact_ptr );

        break;

