        sdss_eng_scr_con_kick_hybr2(ss);
          #endif
        break;


      /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

      case SDSSSCR_CON_STACK_SYNC_UP:

