        sdss_cdma_bsr_in_progress(ss,(sdss_resel_stt_e_type) eng_ptr->con_prm1 );
		    #endif
        break;

      /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

      case SDSSSCR_CON_RESEL_MODE:

