        break;

      /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/


      case SDSSSCR_CON_SET_FLAG:

        /* SET_FLAG( FLAG, SET_VAL ) - Set the FLAG to SET_VAL.
        */
        sdss_set_flag( eng_ptr->ss,
                       (sdss_flag_e_type) eng_ptr->con_prm1,
                       (boolean) eng_ptr->con_prm2 );
        break;

