        /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

        case SDSS_CMP_CRITERIA_NONE:
        case SDSS_CMP_CRITERIA_MAX:
        default:
          return FALSE;
        /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/
