/*===========================================================================

FUNCTION sdss_map_ss_to_as_id

DESCRIPTION
  Maps SD SS to as_id

DEPENDENCIES
  None

RETURN VALUE
  subscription id

SIDE EFFECTS
  None.

===========================================================================*/

sys_modem_as_id_e_type sdss_map_ss_to_as_id(

  sd_ss_e_type       ss
)
{
  #ifdef FEATURE_MMODE_DUAL_SIM
  if (!sdss_is_featuremode(SYS_OVERALL_FEATURE_MODE_MULTISIM))
  {
    return SYS_MODEM_AS_ID_1;
  }

  switch(ss)
  {
    case SD_SS_MAIN:
    #ifdef FEATURE_HDR
    case SD_SS_HYBR_1:
    #endif
      return SYS_MODEM_AS_ID_1;

    #ifdef FEATURE_MMODE_HYBR2_ENABLED 
    case SD_SS_HYBR_2:
      if(sdss_is_sxlte())
      {
        return SYS_MODEM_AS_ID_1;
      }
      else
      {
        return SYS_MODEM_AS_ID_2;
      }
      #endif

    #ifdef FEATURE_MMODE_HYBR3_ENABLED
    case SD_SS_HYBR_3:
      if(sdss_is_sxlte())
      {
        return SYS_MODEM_AS_ID_2;
      }
      else
      {
        return SYS_MODEM_AS_ID_3;
      }
      #endif

    case SD_SS_MAX:
    default:
      SD_ERR_1("Invalid ss %d",ss);
      return SYS_MODEM_AS_ID_MAX;
  }
  #else
  return SYS_MODEM_AS_ID_1;
  #endif
}

