/*===========================================================================

FUNCTION sdss_is_sr_supp

DESCRIPTION
  Check whether the mode band pref of the system record is supported 
  by the current target.

DEPENDENCIES
  None.

RETURN VALUE
  Boolean.

SIDE EFFECTS
  None.

===========================================================================*/
boolean    sdss_is_sr_supp(
  sdsr_s_type         *sr_ptr
)
{
  if (sr_ptr)
  {
    return sdss_is_supp_mode_band(sr_ptr->sys.mode,
                                  sr_ptr->sys.band,
                                  sdsr_map_sys_rec_lte_band_to_lte_band_pref(sr_ptr->sys.lte_band),
                                  SD_SS_BAND_PREF_NONE);
  }
  return FALSE;
} 

/*===========================================================================

FUNCTION sdss_srch_lst_is_valid

DESCRIPTION
  Check whether the SRCH_LST is not empty and can be acquired with 
  current prefs.

RETURN VALUE
  Boolean

===========================================================================*/
boolean sdss_srch_lst_is_valid(
  sd_ss_e_type         ss,
  sdss_cmp_criteria_e_type cmp_criteria
)
{
  boolean is_valid = (sdsr_list_cnt(sdss_sr_list_map2(ss, SDSR_SRCH_LST)) != 0 &&
                      sdss_is_sr_supp(sdsr_list_get_ptr(sdss_sr_list_map2(ss, SDSR_SRCH_LST), 0)));
  
  switch (cmp_criteria)
  {
    case SDSS_CMP_CRITERIA_EQUAL_TO:
      return is_valid;

    /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

    case SDSS_CMP_CRITERIA_NOT_EQUAL_TO:
      return !is_valid;

    /*- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -*/

    case SDSS_CMP_CRITERIA_NONE:
    case SDSS_CMP_CRITERIA_MAX:
    default:
      SD_MSG_HIGH_1("Invalid cmp_criteria %d", cmp_criteria);
      return FALSE;
  }
}